from flask import Flask, render_template, request
from wx.lib.agw.hypertreelist import method


class Panino:
    def __init__(self, panino, contorno, bibita, prezzo, url):
        self.panino = panino
        self.contorno = contorno
        self.bibita = bibita
        self.prezzo = prezzo
        self.url = url

    def __str__(self):
       return f"panino = {self.panino} contorno = {self.contorno} bibita = {self.bibita}"

class ordine:
    def __init__(self, panino, numeroP, prezzo):
        self.panino = panino
        self.numeroP = numeroP
        self.prezzo = prezzo

    def __str__(self):
        return f"panino = {self.panino} numeroP = {self.numeroP}"

p1 = Panino("Primavera", "pomodori", "acqua", 10, "data:image/jpeg;base64,")
p2 = Panino("Estate", "patate", "cocacola", 12, "data:image/jpeg;base64,")
p3 = Panino("Inverno", "patate", "cocacola", 14, "data:image/jpeg;base64,")
listaP = [p1, p2, p3]

app = Flask(__name__)

@app.route("/")
def store():
    return render_template("negozio.html", lista = listaP)

@app.route("/processaN", methods = ["POST", "GET"])
def processaN():
    if request.method == 'POST':

        listaD = request.form.getlist('numeroPezzi')

        listaO = []
        indice = 0
        somma = 0
        listaR = []
        for i, ord in enumerate(listaD):

            if ord != "0":
                listaO.append(listaP[i])
                somma += int(listaP[i].prezzo) * int(ord)
                o1 = ordine(listaP[i].panino, ord, listaP[i].prezzo)
                listaR.append(o1)




        print(somma)


        return render_template("recap.html", somma = somma, lista = listaR)







